// Helper to find a marketing page by slug
export function getMarketingPageBySlug(slug: string): MarketingPage | undefined {
  const searchUrl = '/' + slug;
  return mockMarketingPages.find(p => p.url === searchUrl);
}

// Get all marketing page slugs for static generation
export function getAllMarketingSlugs(): string[] {
  return mockMarketingPages.map(p => p.url.slice(1));
}
